datapack.snippets.forwardSpreadLib:
#> SpreadLib
# @private
#declare tag SpreadMarker

# 拡散させるEntityを召喚する
    summon marker ~ ~ ~ {Tags:["SpreadMarker"]}
# ステータス設定
    data modify storage lib: Argument.Distance set value ${1:<distance>}
    data modify storage lib: Argument.Spread set value ${2:<spread>}
# 拡散
    execute as @e[type=marker,tag=SpreadMarker,limit=1] ${3:}run function lib:forward_spreader/${4:<circle|square>}
# 拡散
    execute facing entity @e[type=marker,tag=SpreadMarker,limit=1] ${5:feet anchored eyes positioned ^ ^ ^ }run ${6:<command>}
# リセット
    kill @e[type=marker,tag=SpreadMarker]
